const daysElement=document.getElementById("days"),hoursElement=document.getElementById("hours"),minsElement=document.getElementById("mins"),secondsElement=document.getElementById("seconds"),newYear="30 Jul 2021";function countdown(){const newYearDate=new Date(newYear),currentDate=new Date,totalSeconds=(newYearDate-currentDate)/1e3,days=Math.floor(totalSeconds/3600/24),hours=Math.floor(totalSeconds/3600)%24,mins=Math.floor(totalSeconds/60)%60,seconds=Math.floor(totalSeconds)%60;daysElement.innerHTML=days,hoursElement.innerHTML=TimeChange(hours),minsElement.innerHTML=TimeChange(mins),secondsElement.innerHTML=TimeChange(seconds)}function TimeChange(time){return time<10`0${time}`}